%fkind = (    "txt" => "Text file",    "pl" => "Perl script",    "htm" => "Hypertext document",    "html" => "Hypertext document");print "請輸入檔名。\n";$fname = <STDIN>;$errorstr = "";if($fname ne "\n"){    if($fname =~ /(.+)\.(.+)\n/){        print "\n檔名：" . $fname;        $fext = $2;        print "副檔名：" . $fext . "\n";        $fext =~ tr/A-Z/a-z/;        if($fkind{$fext} ne ""){            print "(" . $fkind{$fext} . ")\n";        }    }else{        $errorstr = "不明的形式。"    }    }else{    $errorstr = "沒有輸入。";}if($errorstr ne ""){    print "錯誤：" . $errorstr . "\n";}