$filename[0] = "dog.txt";$filename[1] = "rabbit.txt";$str1 = "dog";$str2 = "rabbit";open(IN, "<" . $filename[0])     || die "讀入用檔案$filename[0]的開啟已失敗。";open(OUT, ">" . $filename[1])     || die "寫出用檔案：$filename[1]的開啟已失敗。";print "搜尋字串：$str1\n";print "置換後字串：$str2\n";$i = 0;while(<IN>){    while(/$str1/g){        $i++;    }    s/$str1/$str2/g;    print OUT $_;}print "已置換$i個字串。\n";close(IN);close(OUT);