$animal = "cat and dog and rabbit";$dislike = "dog";print "I like $animal.\n";$a = index($animal, $dislike);if($a != -1){    @arr = split(/ and /, $animal);    $animal = "";        foreach(@arr){        if($_ ne $dislike){            $animal .= $_ . " and ";        }    }    $animal = substr($animal, 0, (length($animal) - 5));        if(length($animal) != 0){        print "I like $animal.\n";    }}